import { O365TeamTabJson } from './__json__/o365-team-tab-json';

/**
 * O365 Team Tab
 */
/* istanbul ignore next: autogenerated */
export class O365TeamTab {

  constructor(private _json: O365TeamTabJson) {
  }

  /**
   * Get the Team Tab display name
   * @returns {string}
   */
  get contentUrl(): string {
    return this._json.content_url;
  }

  /**
   * Get the Team Tab display name
   * @returns {string}
   */
  get displayName(): string {
    return this._json.display_name;
  }

  /**
   * Get the native VBO id for this Team Tab
   * @returns {string}
   */
  get nativeId(): string {
    return this._json.native_id;
  }

  /**
   * Get the Team Tab type
   * @returns {string}
   */
  get type(): string {
    return this._json.type;
  }

  /**
   * Get the json representation of this class.
   * @returns {O365TeamTabJson}
   */
  get json(): O365TeamTabJson {
    return Object.assign({}, this._json);
  }

  /**
   * Get the string representation of this class.
   * @returns {string}
   */
  toString(): string {
    return JSON.stringify(this._json, undefined, 2);
  }
}
